import React, { Component } from 'react'
import { Header, Grid } from 'semantic-ui-react'
import { AreaChart, XAxis, YAxis, CartesianGrid, Tooltip, Area } from 'recharts';
import { getInitialData } from "./../../DataProvider";

export default class Charts extends Component {
    render() {
        this.setState({
            data: getInitialData()
        });
        const data = get

        function createData(array){
            let data = []
            array.forEach(element => {
                let newElement = { "value": element };
                data.push(newElement);
            });
            return data;
        }

        const firstData = createData(this.state.data[0]);

        const secondData = createData(this.state.data[1]);

        const thirdData = createData(this.state.data[2]);

        const fourthData = createData(this.state.data[3]);

        const fifthData = createData(this.state.data[4]);

        const colorDefinition = (
            <linearGradient id="colorValue" x1="0" y1="0" x2="0" y2="1">
                <stop offset="5%" stopColor="#8884d8" stopOpacity={0.8} />
                <stop offset="95%" stopColor="#8884d8" stopOpacity={0} />
            </linearGradient>
        )

        return (
            <div className="Charts">
                <Header as='h2' content='DCM - Data Centering Middleware' subheader='Charts' dividing />

                <Grid columns={2} centered>
                    <Grid.Row>
                        <AreaChart width={730} height={250} data={firstData}
                            margin={{ top: 10, right: 30, left: 0, bottom: 0 }}>
                            <defs>
                                {colorDefinition}
                            </defs>
                            <XAxis />
                            <YAxis />
                            <CartesianGrid strokeDasharray="3 3" />
                            <Tooltip />
                            <Area type="monotone" dataKey="value" stroke="#8884d8" fillOpacity={1} fill="url(#colorValue)" />
                        </AreaChart>

                        <AreaChart width={730} height={250} data={secondData}
                            margin={{ top: 10, right: 30, left: 0, bottom: 0 }}>
                            <defs>
                                {colorDefinition}
                            </defs>
                            <XAxis />
                            <YAxis />
                            <CartesianGrid strokeDasharray="3 3" />
                            <Tooltip />
                            <Area type="monotone" dataKey="value" stroke="#8884d8" fillOpacity={1} fill="url(#colorValue)" />
                        </AreaChart>
                    </Grid.Row>

                    <Grid.Row>
                        <AreaChart width={730} height={250} data={thirdData}
                            margin={{ top: 10, right: 30, left: 0, bottom: 0 }}>
                            <defs>
                                {colorDefinition}
                            </defs>
                            <XAxis/>
                            <YAxis />
                            <CartesianGrid strokeDasharray="3 3" />
                            <Tooltip />
                            <Area type="monotone" dataKey="value" stroke="#8884d8" fillOpacity={1} fill="url(#colorValue)" />
                        </AreaChart>

                        <AreaChart width={730} height={250} data={fourthData}
                            margin={{ top: 10, right: 30, left: 0, bottom: 0 }}>
                            <defs>
                                {colorDefinition}
                            </defs>
                            <XAxis />
                            <YAxis />
                            <CartesianGrid strokeDasharray="3 3" />
                            <Tooltip />
                            <Area type="monotone" dataKey="value" stroke="#8884d8" fillOpacity={1} fill="url(#colorValue)" />
                        </AreaChart>
                    </Grid.Row>

                    <Grid.Row>
                        <AreaChart width={730} height={250} data={fifthData}
                            margin={{ top: 10, right: 30, left: 0, bottom: 0 }}>
                            <defs>
                                {colorDefinition}
                            </defs>
                            <XAxis />
                            <YAxis />
                            <CartesianGrid strokeDasharray="3 3" />
                            <Tooltip />
                            <Area type="monotone" dataKey="value" stroke="#8884d8" fillOpacity={1} fill="url(#colorValue)" />
                        </AreaChart>
                    </Grid.Row>
                </Grid>


            </div>
        )
    }
}
